    protected override async Task OnInitializedAsync() {
        family = familyData.GetFamily(Address, Number);
        familyMembers = family.GetAllMembers();
        filteredFamilyMembers = familyMembers;

        pets = family.Pets;
        filteredPets = pets;
        
